function paletteInit(){
    var $ = go.GraphObject.make;

    var myPalette =
    $(go.Palette, "paletteDiv");

  // the Palette's node template is different from the main Diagram's
  myPalette.nodeTemplate =
    $(go.Node, "Horizontal",
        {resizable: true, resizeObjectName: "SHAPE"},
      $(go.Shape,
        { width: 140, height: 40, fill: "white", name: "SHAPE" },
        new go.Binding("fill", "color")),
      $(go.TextBlock,
        new go.Binding("text", "color"))
    );

    // the list of data to show in the Palette
  myPalette.model.nodeDataArray = [
    { key: "C", color: "cyan", width: 140, height: 40 },
    { key: "LC", color: "lightcyan", width: 140, height: 40 },
    { key: "A", color: "aquamarine", width: 140, height: 40 },
    { key: "T", color: "turquoise", width: 140, height: 40 },
    { key: "PB", color: "powderblue", width: 140, height: 40 },
    { key: "LB", color: "lightblue", width: 140, height: 40 },
    { key: "LSB", color: "lightskyblue", width: 140, height: 40 },
    { key: "DSB", color: "deepskyblue", width: 140, height: 40 }
  ];
}